import React, { Component } from "react";
import ReactDOM from "react-dom";
import { Header, Segment } from 'semantic-ui-react'
import { Statistic } from 'semantic-ui-react'
import { Form } from 'semantic-ui-react'
const qs = require("qs");
const axios = require("axios");

const options = [
  { key: 'ccc', text: 'ccc', value: 'ccc' },
  { key: 'com', text: 'com', value: 'com' },
]

class App extends Component {
  state = {
    authToken: "",
    accountNumber: "",
    walletAddress: "",
    walletCoins: [{}, {}]
  };

  async componentDidMount() {
    this.getAuthToken();
    this.getWalletInformation();
    this.getBalance();
  }

  getAuthToken = async () => {
    const response = await axios({
      method: "post",
      url:
        "https://devlogin.commercio.app/auth/realms/commercio/protocol/openid-connect/token",
      data: qs.stringify({
        client_id: "dev.commercio.app",
        grant_type: "password",
        scope: "openid",
        username: "alessandromusolino@outlook.it",
        password: "aM15081996/",
      }),
      headers: {
        "content-type": "application/x-www-form-urlencoded;charset=utf-8",
      },
    }).then((response) => {
      localStorage.setItem("authToken", response.data.id_token);
      this.setState({
        authToken: "Bearer " + localStorage.getItem("authToken"),
      });
    });
  };

  getWalletInformation = async () => {
    const response = await axios({
      method: "get",
      url: "https://dev-api.commercio.app/v1/wallet/address",
      headers: {
        "accept": "application/json",
        "Authorization": `Bearer ${localStorage.getItem("authToken")}`,
      },
    }).then((response) => {
      localStorage.setItem("walletAddress", response.data.address);
      this.setState({
        accountNumber: response.data.account_number,
        walletAddress: response.data.address,
      });
    });
  };

  getBalance = async () => {
    const response = await axios({
      method: "get",
      url: "https://dev-api.commercio.app/v1/wallet/balance",
      headers: {
        "accept": "application/json",
        "Authorization": `Bearer ${localStorage.getItem("authToken")}`,
      },
    }).then((response) => {
      this.setState({
        walletCoins: response.data.amount
      });
    });
  };

  transfer = async () => {
    const response = await axios({
      method: "post",
      url: "https://dev-api.commercio.app/v1/wallet/transfers",
      headers: {
        "accept": "application/json",
        "Authorization": `Bearer ${localStorage.getItem("authToken")}`,
        "content-type": "application/json",
      },
      data: {
        "amount": [
          {
            "amount": "1000000",
            "denom": "uccc"
          }
        ],
        "back_url": "http://example.com/callback",
        "recipient": localStorage.getItem("walletAddress")
      }
    }).then((response) => {
      console.log(response);
    });
  };

  render() {
    return (
      <div>
      <link
        async
        rel="stylesheet"
        href="https://cdn.jsdelivr.net/npm/semantic-ui@2/dist/semantic.min.css"
      />

      <Segment clearing>
          <Header as='h2' floated='right'>
            Account Number: {this.state.accountNumber}
          </Header>
          <Header as='h2' floated='left'>
            Account Address: {this.state.walletAddress}
          </Header>
        </Segment>

        <Statistic.Group size='tiny'>
          <Statistic>
            <Statistic.Value>{this.state.walletCoins.at(0).amount}</Statistic.Value>
            <Statistic.Label>{this.state.walletCoins.at(0).denom}</Statistic.Label>
          </Statistic>
          <Statistic>
            <Statistic.Value>{this.state.walletCoins.at(1).amount}</Statistic.Value>
            <Statistic.Label>{this.state.walletCoins.at(1).denom}</Statistic.Label>
          </Statistic>
        </Statistic.Group>

        <hr />

        <Form>
          <Form.Group widths='equal'>
            <Form.Input fluid label='Recipient' placeholder='Address' />
            <Form.Input fluid label='Amount' placeholder='Amount' />
            <Form.Select
              fluid
              label='Denom'
              options={options}
              placeholder='Denom'
            />
          </Form.Group>
          <Form.Group inline>
            <label>Size</label>
            <Form.Radio
              label='Small'
              value='sm'
              checked={value === 'sm'}
              onChange={this.handleChange}
            />
            <Form.Radio
              label='Medium'
              value='md'
              checked={value === 'md'}
              onChange={this.handleChange}
            />
            <Form.Radio
              label='Large'
              value='lg'
              checked={value === 'lg'}
              onChange={this.handleChange}
            />
          </Form.Group>
          <Form.TextArea label='About' placeholder='Tell us more about you...' />
          <Form.Checkbox label='I agree to the Terms and Conditions' />
          <Form.Button>Submit</Form.Button>
        </Form>

      </div>
    );
  }
}

export default App;
