import React, { Component } from "react";
import ReactDOM from "react-dom";
const qs = require("qs");
const axios = require("axios");

class App extends Component {
  state = {
    authToken: "",
    walletAddress: "",
  };

  constructor(props) {
    super(props);
    this.getAuthToken();
    this.getWalletAddress();
  }

  getAuthToken = async () => {
    const response = await axios({
      method: "post",
      url:
        "https://devlogin.commercio.app/auth/realms/commercio/protocol/openid-connect/token",
      data: qs.stringify({
        client_id: "dev.commercio.app",
        grant_type: "password",
        scope: "openid",
        username: "alessandromusolino@outlook.it",
        password: "aM15081996/",
      }),
      headers: {
        "content-type": "application/x-www-form-urlencoded;charset=utf-8",
      },
    }).then((response) => {
      this.setState({
        authToken: "Bearer " + response.data.access_token,
      });
    });
  };

  getWalletAddress = async () => {
    const response = await axios({
      method: "get",
      url: "https://dev-api.commercio.app/v1/wallet/address",
      headers: {
        Authorization: "this.state.authToken",
      },
    }).then((response) => {
      this.setState({
        walletAddress: response,
      });
    });
  };

  render() {
    return (
      <div>
        <p>{this.state.authToken}</p>
        <hr />
        <p>{this.state.walletAddress}</p>
      </div>
    );
  }
}

export default App;
