import React, { Component } from "react";
import ReactDOM from "react-dom";
import { Image, Item } from 'semantic-ui-react'
const qs = require("qs");
const axios = require("axios");

class App extends Component {
  state = {
    authToken: "",
    accountNumber: "",
    walletAddress: "",
    walletCoins: [{}, {}]
  };

  async componentDidMount() {
    this.getAuthToken();
    this.getWalletAddress();
  }

  getAuthToken = async () => {
    const response = await axios({
      method: "post",
      url:
        "https://devlogin.commercio.app/auth/realms/commercio/protocol/openid-connect/token",
      data: qs.stringify({
        client_id: "dev.commercio.app",
        grant_type: "password",
        scope: "openid",
        username: "alessandromusolino@outlook.it",
        password: "aM15081996/",
      }),
      headers: {
        "content-type": "application/x-www-form-urlencoded;charset=utf-8",
      },
    }).then((response) => {
      localStorage.setItem("authToken", response.data.id_token);
      this.setState({
        authToken: "Bearer " + localStorage.getItem("authToken"),
      });
    });
  };

  getWalletAddress = async () => {
    const response = await axios({
      method: "get",
      url: "https://dev-api.commercio.app/v1/wallet/address",
      headers: {
        "accept": "application/json",
        "Authorization": `Bearer ${localStorage.getItem("authToken")}`,
      },
    }).then((response) => {
      console.log(response)
      this.setState({
        accountNumber: response.data.account_number,
        walletAddress: response.data.address,
        walletCoins: response.data.coins
      });
    });
  };

  render() {
    return (
      <div>
      <link
        async
        rel="stylesheet"
        href="https://cdn.jsdelivr.net/npm/semantic-ui@2/dist/semantic.min.css"
      />

      <Item.Group>
          <Item>
            <Item.Content>
              <Item.Header as='a'>Account Number</Item.Header>
              <Item.Meta>{this.state.accountNumber}</Item.Meta>
            </Item.Content>
          </Item>

          <Item>
            <Item.Content>
              <Item.Header as='a'>Account address</Item.Header>
              <Item.Meta>{this.state.walletAddress}</Item.Meta>
            </Item.Content>
          </Item>
        </Item.Group>

      </div>
    );
  }
}

export default App;
