import React, { Component } from "react";
import ReactDOM from "react-dom";
const qs = require("qs");
const axios = require("axios");

class App extends Component {
  state = {
    authToken: "",
    walletAddress: "",
  };

  async componentDidMount() {
    this.getAuthToken();
    this.getWalletAddress();
  }

  getAuthToken = async () => {
    const response = await axios({
      method: "post",
      url:
        "https://devlogin.commercio.app/auth/realms/commercio/protocol/openid-connect/token",
      data: qs.stringify({
        client_id: "dev.commercio.app",
        grant_type: "password",
        scope: "openid",
        username: "alessandromusolino@outlook.it",
        password: "aM15081996/",
      }),
      headers: {
        "content-type": "application/x-www-form-urlencoded;charset=utf-8",
      },
    }).then((response) => {
      localStorage.setItem("authToken", response.data.id_token);
      this.setState({
        authToken: "Bearer " + localStorage.getItem("authToken"),
      });
    });
  };

  getWalletAddress = async () => {
    const response = await axios({
      method: "get",
      url: "https://dev-api.commercio.app/v1/wallet/address",
      headers: {
        "accept": "application/json",
        "Authorization": `Bearer ${localStorage.getItem("authToken")}`,
      },
    }).then((response) => {
      this.setState({
        walletAddress: response.data.address,
      });
    });
  };

  render() {
    return (
      <div>
        <p>Address: {this.state.walletAddress}</p>
      </div>
    );
  }
}

export default App;
